CREATE TABLE `dh_info` (
  `infoId` bigint(20) NOT NULL auto_increment,
  `title` varchar(500) default NULL,
  `content` text,
  `seoTitle` varchar(500) default NULL,
  `seoKey` varchar(500) default NULL,
  `seoDescription` text,
  `catId` bigint(20) default NULL,
  `seqNo` smallint(2) default NULL,
  `createTime` datetime default NULL,
  `putTop` smallint(2) default NULL,
  `source` varchar(500) default NULL,
  `author` varchar(200) default NULL,
  `issueDate` date default NULL,
  `imgPath` varchar(500) default NULL,
  PRIMARY KEY  (`infoId`)
) ENGINE=MyISAM AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

