INSERT INTO `dh_info` VALUES (1,'联系我们','<p>\r\n	国际财务策划师公会中国北京代表处<br />\r\n地址：北京市朝阳区对外经贸大学博学楼<br />\r\n邮政编码：100029 <br />\r\n电 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  话：010-64492161<br />\r\n邮 &nbsp; &nbsp; &nbsp;&nbsp;箱：<span style=\"color:#003399;\"><u>iarfp2006@163.com</u></span><br />\r\n网 &nbsp; &nbsp; &nbsp;&nbsp;址：<span style=\"color:#003399;\"><u>www.</u></span><span style=\"color:#003399;\"><u>iarfp.com.cn</u></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">工作时间：</span><br style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\" />\r\n<span style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">周一至周五：上午9:00-12:00&nbsp;&nbsp;&nbsp; 下午2:00-5:00</span><br style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\" />\r\n<span style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">周六日及公众假期，请使用留言信箱或发送邮件至<span style=\"white-space:normal;color:#003399;\"><u>iarfp2006@163.com</u></span></span><span style=\"color:#2F2F2F;font-family:Arial, Verdana, 宋体;letter-spacing:1px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">，我们将尽快与您联系，多谢合作！</span> \r\n</p>\r\n<p>\r\n	<img src=\"/resources/kindeditor/attached/image/20141016/20141016080047_82265.jpg\" style=\"line-height:1.5;\" /> \r\n</p>','','','',30,0,'2014-10-16 08:01:04',0,'本站','管理员','2014-10-16',''),(2,'合作交流','<p>\r\n	<br />\r\n</p>','','','',31,0,'2014-10-16 08:07:37',0,'本站','管理员','2014-10-16',''),(10,'企业内训','<div style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"white-space:nowrap;\">企业内训，是本会认可的持续进修方式之一，尤其是本会会员较为集中的企事业单位及金融机构，开展以企业内训为CPD持续进修方式之前，该企业应向IARFP申请成为企业会员。</span><br />\r\n<span style=\"white-space:nowrap;\">&nbsp;</span><br />\r\n<span style=\"white-space:nowrap;\">金融机构</span><br />\r\n<span style=\"white-space:nowrap;\">高端金融理财（银行、证券、保险）——客户关系管理课程</span><br />\r\n<span style=\"white-space:nowrap;\">私人银行集流课程</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">专业投资</span><br />\r\n<span style=\"white-space:nowrap;\">资本运营与私募股权投资高级课程</span><br />\r\n<span style=\"white-space:nowrap;\">企业财务管理与合理避税课程</span><br />\r\n<span style=\"white-space:nowrap;\">非财务人员的财务管理课程</span><br />\r\n<span style=\"white-space:nowrap;\">证券期货及金融衍生品投资高级课程</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">企业管理</span><br />\r\n<span style=\"white-space:nowrap;\">项目融资与项目管理高级课程</span><br />\r\n<span style=\"white-space:nowrap;\">宏观经济学与货币银行学高级课程</span><br />\r\n<span style=\"white-space:nowrap;\">企业上市并购过程中的税务问题</span><br />\r\n<span style=\"white-space:nowrap;\">房地产投资与营销案例高级课程</span><br />\r\n<span style=\"white-space:nowrap;\">中国内地企业赴境外上市（香港、美国、新加坡、英国）的法律法规问题</span><br />\r\n<span style=\"white-space:nowrap;\">家族企业理财与集团经营（个性化服务）课程</span><br />\r\n<br />\r\n</div>','','','',7,0,'2014-10-31 17:05:02',0,'本站','管理员','2014-10-31',''),(3,'AIRFP课程','<p>\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:14pt;\"><span style=\"font-family:宋体;font-size:14pt;\"> </span></span></strong>\r\n</p>\r\n<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:14pt;\">对外经贸大学注册财务策划师（RFP）招生简章</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;\"> <span style=\"font-size:10.5pt;\"> <span style=\"font-size:10.5pt;\">一张通行全球的专业资质</span></span></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"><br />\r\n注册财务策划师（RFP）所涉及的专业技能就包括个人理财规划及企业资本运作两大方面内容，区别于传统的金融财务人员，注册财务策划师专注于个人和企业资产的保值、增值，并通过投融资运作、税务策划、风险管理、上市并购等手段实现资本目标，被称为21世纪通行全球的专业资质。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\"> 一把打开浩瀚资本市场大门的金钥匙</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">根据CVSource数据统计，2010年共有490家中国企业实现IPO，相比2009年增长155.7%；融资金额共计1068.75亿美元，环比增长88.0%。2010年中国企业IPO数量及融资规模均超越了2007年228家企业上市、融资总额969.23亿美元的历史高位。2010年人均GDP超过4000美元，正式进入理财黄金期。企业资本运营成为必修课，专业金融资质成为资本市场“金钥匙”。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"> <span style=\"color:#FF0000;\"> 一个开拓事业新前景的平台</span></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">国际财务策划师公会倾力打造“第二桶金国际金融研究中心”与“华赢项目数据分析师事务所”，加入RFP，我们将为您提供全方位的服务与发展平台。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">2006年，国际财务策划师公会（IARFP）联合对外经济贸易大学、上海交通大学、重庆大学、华南理工大学、湖南大学等机构，推出财务策划“千人计划”，打造中国顶级财务策划专业队伍。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">依托对外经济贸易大学雄厚的金融学科教学力量，凭借国际财务策划师公司在国际上的影响力，汇集了国内外一流教授资源和金融界专家资源，以学习理论知识和提高实践能力为目标，课程重点强调金融投资实践应用，以“注册财务策划师（RFP）”的必考科目为教学蓝本，<span style=\"color:#FF0000;\">保证学员在最短时间内拿到“全球第一黄金职业”RFP证书。</span><span style=\"color:black;font-size:10.5pt;\">最终培养具备金融投资策划、财税筹划、资本运营、融资上市、风险管理等能力，成为适应经济发展的高层次专业管理人才。</span></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:14pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:14pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">一、课程体系</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-align:center;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<table class=\"table_border ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"647\" align=\"center\" height=\"718\" style=\"border-spacing:0px;\">\r\n	<tbody>\r\n		<tr>\r\n			<td height=\"52\" width=\"180\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong><span style=\"font-size:10.5pt;\">模块设置</span></strong> \r\n			</td>\r\n			<td height=\"52\" width=\"336\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong><span style=\"font-size:10.5pt;\">模块释义</span></strong> \r\n			</td>\r\n			<td height=\"52\" width=\"131\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong><span style=\"font-size:10.5pt;\">课程设置</span></strong> \r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块一<br />\r\n财务策划概论</span> \r\n			</td>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">帮助学员熟悉财务策划学习工具和方法，掌握财务分析和决策所需的基本理论和工具，明确“财富自由”的象征与企业战略规划。</span> \r\n				</p>\r\n			</td>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">财务策划概念 财务策划实施纲要 评估风险容忍度 投、融资策划与风险控制<span style=\"color:#FF0000;font-size:10.5pt;\">公司理财 奢侈品消息及投资</span> </span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\"></span> \r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"72\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块二<br />\r\n财务策划实务</span> \r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">教授学员更实用的财务策划管理工具与方案书制作精要，掌握专业的资讯来源与客户沟通能力，同时提升学员市场调研及经济效益指标分析与方案比选能力。</span> \r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">财务分析与预算管理 现金及债务策划 企业投资与融资 企业并购与重组 企业营运资金</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"79\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块三<br />\r\n税务策划及财务管理</span> \r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">帮助学员掌握税务策划的原理与过程，了解最新税务筹划的方法与空间，培养非财务因素的税务策划意识。 </span> \r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">企业财务管理环境 财务规划与控制 筹资管理 资本金筹资 负债筹资 政策对税务的计划 企业财务管理环境 财务规划与控制 筹资管理 资本金筹资 负债筹资 政策对税务的计划</span> \r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"75\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块四<br />\r\n融资策划</span> \r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">给予学员更开阔的融资视野，审视自身企业所处环境和融资空间，并相应制定切实有效的融资策略。</span> \r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">如何策划融资 私募股权基金运作 创业版、第三版上市 项目融资商业计划书制作 海外融资与并购 如何进行项目包装</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块五<br />\r\n投资策划</span> \r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">加深学员对投资领域的理解和提高学员的投资分析能力和素养。通过学习，学员将会熟知基本金融理念，如货币的时间价值，财务报告的作用，以及股东权益的概念。</span> \r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">风险与分散投资模式 国、内外证劵与债务市场 资产配置 金融衍生工具 投资组合理论 资本结构与杠杆</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"74\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<span style=\"font-size:10.5pt;\">模块六<br />\r\n保险策划与风险管理</span> \r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">帮助学员掌握保险策划的应用性理论和实务工具，同时通过学习掌握大量行业数据，运用科学的计算工具，将经济学原理与数学模型结合，进行科学合理的定量分析，预测项目未来的风险，为项目做出科学的投资与管理决策。</span> \r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					<span style=\"font-size:10.5pt;\">风险管理概述 企业财产保险策划 家庭财产保险策划 人身保险策划 运输货物保险策划 团体信用人寿保险</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">二、课程特色：</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"><span style=\"color:#FF0000;\">权威的国际认证：</span>IRFP培训是权威的认证，受到了国际、国内的广泛认可，与很多知名的协会或院校都签署了互惠认可协议。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"><span style=\"color:#FF0000;\"> 强大的师资阵容：</span>师资主要来自注册财务策划师公会及业内权威的、实战派理财专家、顾问和教授，均拥有15年以上财务策划实战与讲授经验，眼光独到，别具风格，生动活泼，极富实用性和吸引力。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\">先进的教学模式：</span><span style=\"font-size:10.5pt;\">整个课程通过案例研究和项目分析、研讨，为学员提供资本运作、财务策划、税务策划及企业与个人理财等领域的专业知识。理论+实战案例+方案规划研讨，互动授课，突出实践与应用。边学、边练、边制定财务规划方案。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"><span style=\"color:#FF0000;\">广阔的资源平台：</span>享受RFP分布广泛的会员网络，可参加IARFP公会组织的“金领会”论坛和联谊活动，及时获得大量的理财资讯，有机会到大型企业、香港及海外著名金融机构参观、考察和实践；还可参加学校的讲座、论坛及沙龙活动，扩大人脉。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">三、师资力量</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">全部师资均拥有15年以上财务策划实战与讲授经验，眼光独到，生动活泼。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">四、课程优势</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\">★ 对接金融学硕士学位</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"><span lang=\"EN-US\">RFP</span>证书持有人，可直接攻读对外经济贸易大学金融学专业在职课程研修班，免修所有专业课，只学<span lang=\"EN-US\">6</span>门学位课程，符合相关条件的学员，可申请金融学专业经济学硕士学位。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\">★ 对接英国会计师认证</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">RFP证书持有人，最高12门课程豁免，完成剩下的2门IFA课，考试合格，可申请英国注册财务会计师资格（IFA，1916），取得英国法定会计权。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\">★ 庞大的学员交流平台</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">IARFP不定期安排精英俱乐部活动，包括酒会、沙龙、座谈会、户外拓展活动等丰富多彩之形式，提供会员与国内外知名金融财经人士、高端从业者、行业翘楚近身沟通学习之机会，让您尽享中国最高端的“财脉资源”。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">五、学习方式</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">采用<span style=\"color:#FF0000;\">“专业教学+实战模拟+优质服务”</span>的培训模式，目的是为了提高学员独立、全面的处理各种复杂的金融问题的能力，提升职场竞争力，成为真正的资本运作大师。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">六、证书颁发</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><br />\r\n<span style=\"font-size:10.5pt;\"> ★ 考核合格，国际财务策划师公会（IARFP）颁发“注册财务策划师”会员资格证书，证书及名片上印有<span style=\"color:#FF0000;\">“注册财务策划师国际会员”</span>标识，国际认证，全球80多个国家认可。</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\"></span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">七、招生人数</span> </strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:10.5pt;\">小班授课、限额40人，额满即止。</span> </strong>\r\n</p>\r\n<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"><span style=\"color:#FF0000;font-size:10.5pt;\"> \r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:25px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">注：2014年秋季班报名火热进行中，名额有限,欲报从速！</span> \r\n</p>\r\n</span> \r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"color:#FF0000;font-size:10.5pt;\"></span><span lang=\"EN-US\" style=\"line-height:25px;font-family:Arial;font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"line-height:25px;color:#FF0000;font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\">八、招生对象</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">大中型企业财务总监、财务经理等中高层人员；</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">大中型企业总经理、副总经理以及从事公司理财、资本运营、风险管理人士；</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">提供理财咨询的其他专业人员如注册会计师、律师；</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">对金融行业有兴趣、有意成为理财顾问的各行业从业人员；</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\">九、研修费用</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<span style=\"font-size:10.5pt;\"> \r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:25px;text-indent:24pt;\">\r\n	<span lang=\"EN-US\">1</span>、报名费<span lang=\"EN-US\">200</span>元，学费人民币<span lang=\"EN-US\">19800</span>元（所交学费不包括学习期间资料费；注册财务策划师<span lang=\"EN-US\">RFP</span>考试费）。<span lang=\"EN-US\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:25px;text-indent:24pt;\">\r\n	<span lang=\"EN-US\">2</span>、资料教材费：<span lang=\"EN-US\" style=\"font-family:Arial;\">300</span>元左右，学生自理。<span lang=\"EN-US\"><o:p></o:p></span> \r\n</p>\r\n</span> \r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span><span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\">十、上课时间</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">开课日期：滚动开课</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">学制：6个月</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\">十一、上课地点</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">对外经贸大学院内</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<span style=\"font-size:10.5pt;\">十二、申报方式</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">1、在线报名、现场</span><span style=\"font-size:10.5pt;\">审核（现场审核需提交身份证原件、学历证书原件及复印件各3份，一寸蓝底照片8张）</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">2、现场报名（需提交身份证原件、学历证书原件及复印件各3份，一寸蓝底照片8张。</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\"></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">报名地址：对外经贸大学博学楼10层1001B室</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">QQ在线咨询：1300890682</span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">注册财务策划师（RFP）官方网站：<a href=\"http://uibe.iarfp.org.cn/\" style=\"color:#555054;text-decoration:none;\">http://uibe.iarfp.org.cn/</a></span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">联系电话：<span style=\"color:#FF0000;\">010-64492161、64494179</span> </span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<span style=\"font-size:10.5pt;\">联系人：汪老师 </span> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:34px;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n</strong> \r\n<p>\r\n	<br />\r\n</p>','','','',4,0,'2014-10-16 21:07:05',0,'本站','管理员','2014-10-16',''),(4,'公会介绍','<p>\r\n	<span style=\"background-color:#FFFFFF;color:#E53333;font-family:Arial, Verdana, 宋体, sans-serif;font-size:16px;line-height:1.5;\"><strong>国际财务策划师公会</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">注册财务策划师RFP专业名衔始用于1984年，从那时起，在英国一直被尊誉为专业财务策划与水平的标志。目前除了英国之外，RFP会员遍布80多个国家和地区，人数超过68,000人。</span> \r\n</p>\r\n<p>\r\n	国际财务策划师公会（INTERNATIONAL ASSOCIATION OF REGISTERED FINANCIAL PLANNERS）是全球范围颁授注册财务策划师资格名衔的专业机构，也是世界上唯一用，最早制定并颁发IARFP职业标准的团体之一。本会的主要宗旨是为社会提供专业认可的财务策划人才，并为这些专业的财务策划师授予注册会员资格。\r\n</p>\r\n<p>\r\n	现任公会会长Malcolm.Dean先生，其亦为全球第一大工商业界会计师团体——英国财务会计师公会（IFA）前行政总裁。\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;line-height:1.5;\"><strong>International Association of Registered Financial Planners</strong></span> \r\n</p>\r\n<p>\r\n	R.F.P.(Registered Financial Planner) is a professional financial planning designation and was first used in commerce in 1984 and it has been the hallmark of the professional financial planner in the United Kindom since then. Nowadays, RFP have 68,000 members and fellow members from 80 countries.\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><strong>Vision</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">To be recognized as the premier professional body to represent the highest standard of excellence in the competency of financial planning that benefit the public.</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><strong>Mission</strong></span> \r\n</p>\r\n<p>\r\n	• To promote the profession of financial planning by setting standard of excellence in the competency and practice of financial planning, for those financial planners who aspire to achieve such excellence.\r\n</p>\r\n<p>\r\n	• To promote the IARFP certification as the highest standard for financial planning.\r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;color:#000000;font-family:Arial, Verdana, 宋体, sans-serif;font-size:13px;line-height:22px;\">• To represent the top financial planning，and wealth management practitioners in the financial planning industry in the world.</span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;color:#000000;font-family:Arial, Verdana, 宋体, sans-serif;font-size:13px;line-height:22px;\">• To provide the premiere forum to encourage the development and exchange of advanced financial planning knowledge amongst our members.</span> \r\n</p>\r\n<p class=\"malcolm\" style=\"vertical-align:baseline;font-size:13px;outline:0px;padding:0px;margin-top:20px;margin-bottom:20px;border:0px;line-height:22px;color:#2F2F2F;font-family:Arial, Verdana, 宋体, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<a title=\"点击查看会长资料\" href=\"http://www.rfp.org.cn/malcolm.asp\" target=\"_blank\" style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;color:#0000FF;\"><img title=\"英国财务会计师公会(IFA)会长Malcolm Dean\" alt=\"英国财务会计师公会(IFA)会长Malcolm Dean\" src=\"http://www.rfp.org.cn/images/Mr_Dean_s.gif\" style=\"vertical-align:middle;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px 20px 0px 0px;border:3px double #CCCCCC;width:101px;height:128px;float:left;\" /></a><span style=\"vertical-align:baseline;font-weight:600;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;\"><a title=\"点击查看会长资料\" href=\"http://www.rfp.org.cn/malcolm.asp\" target=\"_blank\" style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;color:#333333;\">J.Malcolm Dean, President</a></span><br />\r\n<span style=\"color:#000000;\">International Association Of Registered Financial Planners</span>. <br />\r\n<span style=\"color:#000000;\">Website:</span> <a href=\"http://www.iarfp.org.uk/\" target=\"_blank\" style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;color:#0000FF;\">www.iarfp.org.uk</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','',27,0,'2014-10-16 21:15:43',0,'本站','管理员','2014-10-16',''),(5,'网站导航','','','','',33,0,'2014-10-18 23:18:07',0,'本站','管理员','2014-10-18',''),(6,'友情链接','','','','',32,0,'2014-10-18 23:20:53',0,'本站','管理员','2014-10-18',''),(7,'8月份金融机构外汇占款再现负增长','<span style=\"white-space:nowrap;\">中国人民银行最新公布的8月份金融机构外汇占款数据显示，8月末金融机构外汇占款余额为294580.30亿</span><br />\r\n<span style=\"white-space:nowrap;\">元，较上月减少逾311亿元人民币。这是在时隔一个月后再次出现负增长。此前6月份金融机构外汇占款年内</span><br />\r\n<span style=\"white-space:nowrap;\">首现负增长，减少882.8亿元，7月份重回正增长。</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\"> 相关分析认为，8月份我国经济数据整体不及预期，从而导致人民币升值预期较弱，企业结汇意愿不强。</span><br />\r\n<span style=\"white-space:nowrap;\">此外，美国逐渐退出量化宽松政策，美元加息预期增强，这也促进了资本的跨境流动。</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\"> 中国海关此前公布的数据显示，8月份出口同比增长9.4%，进口同比下降2.4%，贸易顺差498.3亿美元，</span><br />\r\n<span style=\"white-space:nowrap;\">改写7月份创下的473亿美元的单月历史纪录。一方面，8月份我国经常项目顺差规模增加，另一方面，外汇占</span><br />\r\n<span style=\"white-space:nowrap;\">款在减少，这也显示出带动资本流动的其他因素作用较为明显。分析人士指出，鉴于目前经济整体情况，预</span><br />\r\n<span style=\"white-space:nowrap;\">计定向宽松政策会继续推进，宏观调控微刺激基调仍将继续。</span><br />\r\n<br />','8月份金融机构外','8月份金融机构外','8月份金融机构外',36,0,'2014-10-20 22:46:50',0,'本站','管理员','2014-10-20',''),(8,'同等学历','<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:10.5pt;\">对外经贸大学金融学专业（注册财务策划师RFP方向）在职课程高级研修双证班招生简章</span></strong>\r\n</p>\r\n<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<b><span style=\"font-family:新宋体;color:red;\"></span></b>&nbsp;\r\n</p>\r\n<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<b><span style=\"font-family:新宋体;color:red;\">大学本科及同等学历免试入学</span></b>\r\n</p>\r\n<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<b><span style=\"font-family:新宋体;color:red;\"></span></b><b><span style=\"font-family:宋体;color:red;\">学习期满成绩合格可同时取得</span></b><b><span lang=\"EN-US\" style=\"font-family:新宋体;color:red;\"><o:p></o:p></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:12pt;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;text-align:center;\">\r\n	<b><span style=\"font-family:宋体;color:red;\">硕士学位和国际注册财务策划师（RFP）资质</span></b><b><span lang=\"EN-US\" style=\"font-family:宋体;\"><br />\r\n<br />\r\n<o:p></o:p></span></b>\r\n</p>\r\n<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>一、课程背景</strong>&nbsp;<br />\r\n经济形态的双轨制要求我们掌握两种经济形态下的知识，经济体制动态转变要求我们不断地更新知识，完善知识结构，改变经营理念，开拓国际化战略思维能力。<span style=\"color:#FF0000;\"><strong>外经贸大学经贸学院与国际财务策划师公会</strong></span>于2011年1月举办金融学硕士财务策划师方向周末课程班。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	RFP是“注册财务策划师”（Registered Financial Planner）的简称，财务策划是指运用科学公正的财务分析程序来对个人或企业的财务计划、投资策略等进行合理的规划与管理，以实现其长期理财和生活目标的专业理财服务，是金融、保险等行业指导投资理财的新兴业务。财务策划师广泛来自于会计师、注册会计师、个人理财顾问、投资顾问、以及银行、保险、证券或财务公司的从业人员。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n&nbsp;注册财务策划师资格名衔授予机构是英国国际财务策划师公会（IARFP）, 总部设立在英国伦敦，是全球范围颁授注册财务策划师资格名衔的专业机构，也是最早制定并颁布注册财务策划师（RFP）职业标准的团体之一。目前除英国外，RFP会员遍布全球80多个国家和地区，包括在中国大陆已拥有6000多名会员。目前，具备国际财务策划专业水准的人才，已被列为国家紧缺人才培训工程，可见该职业是“全球第一黄金职业”。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n注册财务策划师如医生、律师一样，是国际上公认的专业资质。区别于传统的金融财务人员，注册财务策划师专注于个人和企业资产的保值、增值，并通过投融资运作、税务策划、风险管理、上市并购等手段实现资本目标，被称为21世纪的“资本管家”。 据统计，国内财务策划人才缺口超过300万。已被国家人事部门列为紧缺人才之首。在国外，财务策划师年收入达到50万美元，是超越 MBA、CPA的“全球第一黄金职业”。获得RFP证书，意味着获得无可比拟的高薪和令人尊敬的工作性质！\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n&nbsp;　&nbsp;<br />\r\n<strong>二、培养目标：</strong>&nbsp;<br />\r\n<span style=\"font-family:宋体;color:black;\"><strong>依托对外经济贸易大学雄厚的金融学科教学力量，凭借国际财务策划师公司在国际上的影响力，汇集了国内外一流教授资源和金融界专家资源</strong>，以学习理论知识和提高实践能力为目标，学位基础课重点掌握金融学专业理论知识，应对申硕国家考试；专业课程重点强调金融投资实践应用，以“注册财务策划师（RFP）”的必考科目为教学蓝本，<span style=\"color:#FF0000;\"><strong><span style=\"font-size:14pt;\">保证</span>学员在最短时间内拿到“全球第一黄金职业”RFP证书。</strong></span>最终培养具备金融投资策划、财税筹划、资本运营、融资上市、风险管理等能力，成为适应经济发展的高层次专业管理人才。</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:宋体;color:black;\"></span>&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:宋体;color:black;\"></span><strong>三、课程设置：</strong>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	1、学位课程<span style=\"color:#FF0000;\">：（师资：全日制研究生师资）</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n（1）微观经济学 （2）宏观经济学 （3）社会主义经济理论&nbsp;<br />\r\n（4）货币银行学 （5）国际经济学 （6）财政学\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n2、专业课程<span style=\"color:#FF0000;\">：（师资：国际财务策划师公会特聘海外实战专家）</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<table class=\"table_border ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"647\" align=\"center\" height=\"718\" style=\"border-spacing:0px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;font-size:12px;background-color:#FFFFFF;\">\r\n	<tbody>\r\n		<tr>\r\n			<td height=\"52\" width=\"180\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong>模块设置</strong>\r\n			</td>\r\n			<td height=\"52\" width=\"336\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong>模块释义</strong>\r\n			</td>\r\n			<td height=\"52\" width=\"131\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<strong>课程设置</strong>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块一<br />\r\n财务策划概论\r\n			</td>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					帮助学员熟悉财务策划学习工具和方法，掌握财务分析和决策所需的基本理论和工具，明确“财富自由”的象征与企业战略规划。\r\n				</p>\r\n			</td>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					财务策划概念 财务策划实施纲要 评估风险容忍度 投、融资策划与风险控制&nbsp;<span style=\"color:#FF0000;\">公司理财 奢侈品消息及投资</span>&nbsp;\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"72\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块二<br />\r\n财务策划实务\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					教授学员更实用的财务策划管理工具与方案书制作精要，掌握专业的资讯来源与客户沟通能力，同时提升学员市场调研及经济效益指标分析与方案比选能力。\r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					财务分析与预算管理 现金及债务策划 企业投资与融资 企业并购与重组 企业营运资金\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"79\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块三<br />\r\n税务策划及财务管理\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					帮助学员掌握税务策划的原理与过程，了解最新税务筹划的方法与空间，培养非财务因素的税务策划意识。 帮助学员掌握税务策划的原理与过程，了解最新税务筹划的方法与空间，培养非财务因素的税务策划意识。\r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				企业财务管理环境 财务规划与控制 筹资管理 资本金筹资 负债筹资 政策对税务的计划 企业财务管理环境 财务规划与控制 筹资管理 资本金筹资 负债筹资 政策对税务的计划\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"75\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块四<br />\r\n融资策划\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					给予学员更开阔的融资视野，审视自身企业所处环境和融资空间，并相应制定切实有效的融资策略。\r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					如何策划融资 私募股权基金运作 创业版、第三版上市 项目融资商业计划书制作 海外融资与并购 如何进行项目包装\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"69\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块五<br />\r\n投资策划\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					加深学员对投资领域的理解和提高学员的投资分析能力和素养。通过学习，学员将会熟知基本金融理念，如货币的时间价值，财务报告的作用，以及股东权益的概念。\r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					风险与分散投资模式 国、内外证劵与债务市场 资产配置 金融衍生工具 投资组合理论 资本结构与杠杆&nbsp;<span style=\"font-family:宋体;font-size:9pt;\">证券操作实务</span><span style=\"font-family:Arial;font-size:9pt;\">&nbsp;</span><span style=\"font-family:宋体;font-size:9pt;\">风险投资及私募股权投资</span>\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td height=\"74\" align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				模块六<br />\r\n保险策划与风险管理\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					帮助学员掌握保险策划的应用性理论和实务工具，同时通过学习掌握大量行业数据，运用科学的计算工具，将经济学原理与数学模型结合，进行科学合理的定量分析，预测项目未来的风险，为项目做出科学的投资与管理决策。\r\n				</p>\r\n			</td>\r\n			<td align=\"middle\" style=\"margin:0px;padding:0px;border:1px solid #999999;\">\r\n				<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;\">\r\n					风险管理概述 企业财产保险策划 家庭财产保险策划 人身保险策划 运输货物保险策划 团体信用人寿保险\r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	注：注册财务策划师会员或学员只需修读6门学位课程即可申请硕士学位。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">四、课程特色：</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:24pt;background-color:white;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">权威的国际认证</span></b><span style=\"font-family:宋体;font-size:9pt;\">： 通过专业课的学习可以申请考试取得注册财务策划师（IRFP）证书，</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">IRFP</span><span style=\"font-family:宋体;font-size:9pt;\">培训是权威的认证，受到了国际、国内的广泛认可，与很多知名的协会或院校都签署了互惠认可协议。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:22.15pt;background-color:white;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">强大的师资阵容</span></b><span style=\"font-family:宋体;font-size:9pt;\">：师资主要来自注册财务策划师公会及业内权威的、实战派理财专家、<st1:personname productid=\"顾问和\" w:st=\"on\">顾问和</st1:personname>教授，均拥有</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">15</span><span style=\"font-family:宋体;font-size:9pt;\">年以上财务策划实战与讲授经验，眼光独到，别具风格，生动活泼，极富实用性和吸引力。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:24pt;background-color:white;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">先进的教学模式</span></b><span style=\"font-family:宋体;font-size:9pt;\">：整个课程通过案例研究和项目分析、研讨，为学员提供资本运作、财务策划、税务策划及企业与个人理财等领域的专业知识。理论</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">+</span><span style=\"font-family:宋体;font-size:9pt;\">实战案例</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">+</span><span style=\"font-family:宋体;font-size:9pt;\">方案规划研讨，互动授课，突出实践与应用。边学、边练、边制定财务规划方案。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:24pt;background-color:white;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">广阔的资源平台</span></b><span style=\"font-family:宋体;font-size:9pt;\">：享受</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">RFP</span><span style=\"font-family:宋体;font-size:9pt;\">分布广泛的会员网络，可参加</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">IARFP</span><span style=\"font-family:宋体;font-size:9pt;\">公会组织的</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">“</span><span style=\"font-family:宋体;font-size:9pt;\">金领会</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">”</span><span style=\"font-family:宋体;font-size:9pt;\">论坛和联谊活动，及时获得大量的理财资讯，有机会到大型企业、香港及海外著名金融机构参观、考察和实践；还可参加学校的讲座、论坛及沙龙活动，扩大人脉。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:24pt;background-color:white;\">\r\n	<span style=\"font-family:宋体;font-size:9pt;\"></span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>&nbsp;\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<b><span style=\"font-family:宋体;font-size:9pt;\">五、师资力量</span></b><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;text-indent:24pt;background-color:white;\">\r\n	<span style=\"font-family:宋体;font-size:9pt;\">全部师资均拥有</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">15</span><span style=\"font-family:宋体;font-size:9pt;\">年以上财务策划实战与讲授经验，眼光独到，生动活泼。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><br />\r\n</span><strong><span style=\"font-family:宋体;font-size:9pt;\">六、证书与学位：</span></strong><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n1</span><span style=\"font-size:9pt;\">、在职课程研修班的学员，完成</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">“</span><span style=\"font-size:9pt;\">课程设置</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">”</span><span style=\"font-size:9pt;\">中所规定的课程并考试（考核）成绩合格者即可结业。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n2</span><span style=\"font-size:9pt;\">、结业学员获加盖校长签名章、学校钢印和红章的具有在职人员高级研修同等学力水平证明的《对外经济贸易大学研修班结业证书》。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n3</span><span style=\"font-size:9pt;\">、符合申请硕士学位条件的学员（取得国家承认学士学位）可按我校有关规定申请对外经贸大学金融学专业经济学硕士学位。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n4</span><span style=\"font-size:9pt;\">、申报参加国际财务策划师公会（</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">IARFP</span><span style=\"font-size:9pt;\">）安排的全国统一考试通过，国际财务策划师协会公会（</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">IARFP</span><span style=\"font-size:9pt;\">）颁发《注册财务策划师（</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">IRFP</span><span style=\"font-size:9pt;\">）》资格证书，成为其正式会员。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">5</span><span style=\"font-size:9pt;\">、可申报取得英国注册财务会计师</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">AFA</span><span style=\"font-size:9pt;\">资格。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\"><o:p></o:p></span>\r\n</p>\r\n<span lang=\"EN-US\" style=\"color:#013E41;font-size:9pt;white-space:normal;background-color:#FFFFFF;line-height:21px;font-family:Arial;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<br />\r\n</p>\r\n</span><span style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"></span>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<strong><span style=\"font-family:宋体;font-size:9pt;\">七、学制与学费：</span></strong><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n</span><span style=\"font-size:9pt;\">1、学制两年，隔周周末（六、日）上课，遇重大节日顺延。</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">&nbsp;<br />\r\n</span><span style=\"font-size:9pt;\">2、收费标准：报名费200元，学费</span><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">29800</span><span style=\"font-size:9pt;\">元/两年。（所交学费不包拓进入论文阶段的费用及学习期间资料费。）</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<span style=\"font-size:9pt;\">3、资料教材费：500元左右/2年，学生自理。</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<span style=\"font-size:9pt;\">4、<strong><span style=\"color:#FF0000;\">学习期间每门结业考试均不另收取考试费。</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<span style=\"font-size:9pt;\"></span>&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<span style=\"font-size:9pt;\"></span>&nbsp;\r\n</p>\r\n<span lang=\"EN-US\" style=\"color:#013E41;font-size:9pt;white-space:normal;background-color:#FFFFFF;line-height:21px;font-family:Arial;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<strong>八、报名条件：</strong>&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	1、遵纪守法，品行端正，在相关机构、教学、科研、管理等方面做出成绩的在职人员优先录取。<span style=\"line-height:18px;font-family:宋体;color:black;\">&nbsp;<br />\r\n2、大专及以上学历（专业背景不限，有学士学位的学生有资格申请对外经贸大学金融学专业经济学硕士学位证书）。</span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<br />\r\n</p>\r\n</span><span style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\"></span>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<strong><span style=\"font-family:宋体;font-size:9pt;\"></span></strong>&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:white;\">\r\n	<strong><span style=\"font-family:宋体;font-size:9pt;\">九、开课时间：限额招生</span></strong><strong><span lang=\"EN-US\" style=\"font-family:Arial;font-size:9pt;\">40</span></strong><strong><span style=\"font-family:宋体;font-size:9pt;\">人，额满即止！</span></strong><strong><span style=\"font-family:宋体;color:#FF0000;font-size:9pt;\"><br />\r\n</span></strong>\r\n</p>\r\n<strong style=\"color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;line-height:normal;white-space:normal;background-color:#FFFFFF;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;text-indent:2em;\">\r\n	<span style=\"color:#FF0000;font-size:10.5pt;\">注：2014年秋季班报名火热进行中，名额有限,欲报从速！</span>\r\n</p>\r\n</strong><span lang=\"EN-US\" style=\"color:#013E41;font-size:9pt;white-space:normal;background-color:#FFFFFF;line-height:21px;font-family:Arial;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<br />\r\n<strong><span style=\"font-family:宋体;font-size:9pt;\">十、报名手续：</span></strong><span lang=\"EN-US\" style=\"font-size:9pt;\">&nbsp;<br />\r\n1</span><span style=\"font-size:9pt;\">、索取并填写报名登记表；</span><span lang=\"EN-US\" style=\"font-size:9pt;\">&nbsp;<br />\r\n2</span><span style=\"font-size:9pt;\">、提供本人身份证原件与复印件一份、最高学历或学位证书原件与复印件一份；</span><span lang=\"EN-US\" style=\"font-size:9pt;\">&nbsp;<br />\r\n3</span><span style=\"font-size:9pt;\">、提交</span><span lang=\"EN-US\" style=\"font-size:9pt;\">1&nbsp;</span><span style=\"font-size:9pt;\">寸免冠照片</span><span lang=\"EN-US\" style=\"font-size:9pt;\">2</span><span style=\"font-size:9pt;\">张，二寸</span><span lang=\"EN-US\" style=\"font-size:9pt;\">2&nbsp;</span><span style=\"font-size:9pt;\">张；</span><span lang=\"EN-US\" style=\"font-size:9pt;\">&nbsp;<br />\r\n4</span><span style=\"font-size:9pt;\">、交付报名费</span><span lang=\"EN-US\" style=\"font-size:9pt;\">200</span><span style=\"font-size:9pt;\">元；学费</span><span lang=\"EN-US\" style=\"font-size:9pt;\">29800</span><span style=\"font-size:9pt;\">元。</span><span lang=\"EN-US\" style=\"font-size:9pt;\"><br />\r\n5</span><span style=\"font-size:9pt;\">、学校对报名人员进行初步资格审查合格后，发放录取通知书。</span><span lang=\"EN-US\" style=\"font-size:9pt;\"></span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<span lang=\"EN-US\" style=\"font-size:9pt;\"></span>&nbsp;\r\n</p>\r\n<span lang=\"EN-US\" style=\"font-size:9pt;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">十、通过银行汇款交费的学员可将学费汇入以下帐户：</span></b><span lang=\"EN-US\" style=\"font-family:宋体;font-size:12pt;\"><br />\r\n<br />\r\n</span><span lang=\"EN-US\" style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;\">开户行：北京银行和平里支行<span lang=\"EN-US\">&nbsp;<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>户<span lang=\"EN-US\">&nbsp;&nbsp;</span>名：对外经济贸易大学<span lang=\"EN-US\">&nbsp;<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>帐<span lang=\"EN-US\">&nbsp;&nbsp;</span>号：<span lang=\"EN-US\">010903537001201052621—26&nbsp;<o:p></o:p></span></span>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<br />\r\n&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<strong><span style=\"font-size:9pt;\">报名地址：对外经济贸易大学</span><span lang=\"EN-US\" style=\"font-size:9pt;\">博学楼10层1001B室<o:p></o:p></span></strong>\r\n</p>\r\n<span lang=\"EN-US\" style=\"font-size:9pt;\">\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<br />\r\n</p>\r\n</span>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<strong><span style=\"font-family:宋体;font-size:9pt;\">联</span></strong><strong>&nbsp;</strong><strong><span style=\"font-family:宋体;font-size:9pt;\">系</span></strong><strong>&nbsp;</strong><strong><span style=\"font-family:宋体;font-size:9pt;\">人：汪老师</span></strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong><b><span lang=\"EN-US\" style=\"font-size:9pt;\"><br />\r\n</span></b><strong><span style=\"font-family:宋体;font-size:9pt;\">电　　话：</span></strong><strong>010</strong><strong><span style=\"font-family:宋体;font-size:9pt;\">－</span></strong><st1:chmetcnv w:st=\"on\" unitname=\"m\" sourcevalue=\"64492161\" hasspace=\"False\" negative=\"False\" numbertype=\"1\" tcsc=\"0\"><strong>64492161、64494179&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></st1:chmetcnv>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<st1:chmetcnv w:st=\"on\" unitname=\"m\" sourcevalue=\"64492161\" hasspace=\"False\" negative=\"False\" numbertype=\"1\" tcsc=\"0\"><strong><span lang=\"EN-US\" style=\"font-size:9pt;\"></span></strong></st1:chmetcnv><b>QQ在线咨询&nbsp;: 1300890682</b>\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>\r\n</p>\r\n</span></span>','','','',5,0,'2014-10-31 16:56:53',0,'本站','管理员','2014-10-31',''),(9,'AFA课程','<p align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>英国注册财务会计师（AFA）招生简章</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>一、IFA历史<br />\r\n</strong> •&nbsp;创立于1916年（90多年历史的英国财务会计师公会） <br />\r\n•&nbsp;英国第一大为工商业会计师授予资格证明的专业机构 <br />\r\n•&nbsp;世界上历史最长的专业会计团体之一 <br />\r\n•&nbsp;全球最大的财务会计师团体机构 <br />\r\n•&nbsp;国际权威认证，全世界80多个国家拥有68000多名会员 <br />\r\n•&nbsp;IFA会员资格相当于拥有英国会计学士学位 <br />\r\n•&nbsp;IFA专业资格为英国工贸部所认可，可作为英国工作签证的认可资格 <br />\r\n•&nbsp;持有IFA资格的会员，可从事财务会计、管理会计、会计经理及税务等工作\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>二、AFA执业资格<br />\r\n</strong> •&nbsp;外合资格会计师执照的高级财务人士需求激增，必然趋势； <br />\r\n•&nbsp;IFA现任会长为会计团体全球排名第一的ICAEW前首席执行官Eric Anstee先生； <br />\r\n•&nbsp;IFA现在中国会员2000多人，主要是大中型企业财务总监； <br />\r\n•&nbsp;全球500强企业以及大多数上市公司/大型国企是IFA的优质雇主； <br />\r\n•&nbsp;IFA专业资格人士可以申请升读英国/澳洲一流大学硕士学位。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>四、课程特色</strong><br />\r\n•&nbsp;最高可获国际教育奖学金津贴RMB10000元（仅限全国前1000名，且符合IFA相关申请条件）； <br />\r\n•&nbsp;面授+俱乐部活动，轻松自如； <br />\r\n•&nbsp;猎头面对面，开阔眼界，放宽职场视野； <br />\r\n<strong>五、课程对象<br />\r\n</strong> •&nbsp;党政机关、事业单位、公共服务机构会计主管以及财务经理； <br />\r\n•&nbsp;国有企业、大中型企业会计人员，且具备一定职称学历人士。 <br />\r\n•&nbsp;已通过IRFP考试的会员。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>六、IFA课程大纲</strong><br />\r\n<strong>1 财务会计与内控</strong> <br />\r\n•&nbsp;会计与会计环境 <br />\r\n•&nbsp;货币资金与应收款项 <br />\r\n•&nbsp;销售成本与存货 <br />\r\n•&nbsp;国际财务会计报告及财务报表分析 <br />\r\n•&nbsp;收入与利润 <br />\r\n•&nbsp;固定资产、无形资产和其他资产负债 <br />\r\n•&nbsp;债务重组与企业兼并中的会计问题 <br />\r\n<strong>2管理会计</strong> <br />\r\n•&nbsp;成本归集与产品成本计算 <br />\r\n•&nbsp;计划与控制 <br />\r\n•&nbsp;管理决策系统 <br />\r\n•&nbsp;管理会计中的国际问题 <br />\r\n<strong>3 财务管理与金融战略</strong> <br />\r\n•&nbsp;财务计划与预计财务报表 <br />\r\n•&nbsp;跨国公司财务 <br />\r\n•&nbsp;资本投资决策、 营运资本管理 <br />\r\n•&nbsp;资本成本与资本结构决策 <br />\r\n•&nbsp;公司融资及股利政策的制定 <br />\r\n•&nbsp;股东价值测度与管理 <br />\r\n•&nbsp;兼并、收购与股票价值 <br />\r\n<strong>4 新会计准则实施与应用</strong> <br />\r\n了解国际会计准则（IFRS）的核心内容，分析新会计准则（CAS）与国际财务报告准则的联系和差异，掌握新会计准则及国际财务报表的编制，为中外投资者提供真实、公允、可比的会计信息。International Financial Reporting Standard and the New Chinese Accounting Standard To understand the core content of International Financial Reporting Standard (IFRS), to analysis the relationship and difference between the New Chinese Accounting Standard (CAS) and the International Financial Reporting Standard (IFRS), to master how to make financial report under International Financial Reporting Standard (IFRS) and the New Chinese Accounting Standard (CAS), to provide the investors with real, fair and comparable accounting information. <br />\r\n<strong>5 企业税务筹划</strong> <br />\r\n了解企业税务筹划的环境和法制条件，掌握企业税务筹划策略、技巧和方法。在遵守国家法律、法规和税收政策的前提下，进行有效的税务筹划，降低运作成本，实现企业财务利益最大化 <br />\r\n<strong>6 财务风险管理</strong> <br />\r\n充分了解企业在经营管理中建立财务危机预警指标体系的重要性。学会如何识别风险、度量风险及规避风险。课程内容包括经营、产品、利率、汇率风险管理以及综合风险管理等。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>七、IFA课程导师<br />\r\nJ Malcolm Dean</strong> <br />\r\nFIRFP FFA FIAB 前任英国注册财务会计师公会会长 <br />\r\n<strong>江子荣</strong> <br />\r\n香港太平坤士、现任英国注册财务会计师公会副会长，拥有30年会计经验 <br />\r\n<strong>区撄子</strong> <br />\r\n区樱子女士取得管理学硕士，从教20余年，是华南理工大学经济与贸易学院金融硕士研究生导师，广东审计学会理事，主讲华工大MBA《会计学》《财务管理》《税收研究》课程、广州地铁总公司管理咨询顾问、主持华南理工大学社科基金项目、广州市政府项目《工业工程前期工作》项目顾问。 <br />\r\n<strong>李定安教授</strong> <br />\r\n华南理工大学工商管理学院会计学教授，经济与贸易学院任常务副院长，会计硕士生导师，经济博士研究生导师，首批中国注册会计师。 <br />\r\n<strong>Andrew CAPON FCA</strong> <br />\r\nChief Executive, China International Partnerships Ltd. 安德鲁卡彭特许会计師 - 中国国际合作伙伴总裁 <br />\r\n<strong>Dr. Gian CASIMIR PhD</strong> <br />\r\nSenior Lecturer, University of Newcastle, Australia 吉安凱西米爾博士- 澳大利亞 Newcastle 大<strong>学高级講師</strong> <br />\r\n<strong>Paul C. C. CHAN FCPA(Practising)</strong> <br />\r\nManaging Director, World Link CPA Ltd. 陳卓智会计師 - 匯领会计師事务所主席、行政总裁 <br />\r\nRicky CHU ACA(Aust), FCPA(Practising) <br />\r\nManaging Director, Lloyds Pacific Financial Consultants Ltd. 朱强华特许会计師 - 萊司太平洋<strong>顧问有限公司董事总经理 <br />\r\nDr. William KIRSCHENBAUM PhD</strong> <br />\r\nChairman, Triglobal Capital Group 威廉凯斯博士 - 高博金融集团董事长 <br />\r\n<strong>Dr. Stephen KWOK EdD</strong> <br />\r\nChairman, GA Education Group 郭振邦博士 - 宏万教育集团主席 <br />\r\n<strong>Dr. Andy S. W. LAM DUniv, JP</strong> <br />\r\nExecutive Director, Harmony Asset Limited 林兆榮博士、太平紳士 享亞有限公司执行董事 <br />\r\n<strong>Chi Keung LEUNG FCCA, CPA</strong> <br />\r\nVice President, Uhunt Consulting Ltd. 梁炽强会计師 - 优之才(北京)人力资源咨洵顾问有限公司副<strong>总裁 <br />\r\nProf. LI Xiaolin PhD</strong> <br />\r\nDean, China Institute for Actuarial Science Central University of Finance and Economics, China 李晓林教授、博士 - 中央财经大学中国精算研究院院长 <br />\r\nProf. Morris W. Y. MOK <br />\r\nPresident, The Chartered Risk Management Assessor Society 麥伟賢教授 - 特许凤险評估规划師協会会长 <br />\r\n<strong>Dr. Vincent NG DBA, JP</strong> <br />\r\nVisiting Professor, University of Southern Queensland, Australia 吳文全博士、太平紳士 澳大利亞南昆士蘭大學客座教授 <br />\r\nShinji SHIBUYA <br />\r\nChief Representative, Beijing Representative Office Daiwa Securities SMBC Co., Ltd. 涩谷慎志先生 - 大和証券SMBC株式会社北京代表处首席代表 <br />\r\n<strong>Dr. Wai Man WOO PhD, BBS</strong> <br />\r\nManaging Director, Drowland International Investment Ltd. Former Chairman, Po Leung Kuk Hong Kong 胡偉民博士 铜紫荆勋章 –原香港保良局主席 <br />\r\nProf. WU Feng DEcon <br />\r\nDirector, Business-Education Research Center Peking University, China 吴峰教授、博士 - 北京<strong>大学企业与教育研究中心主任 <br />\r\nAlbert S. C. YOUNG MH, BH, FCPA(Practising)</strong> <br />\r\nPrincipal, Albert S. C. Young and Company 楊少銓会计師 荣誉勋章- 楊少銓会计師事务所東主 <br />\r\n<strong>Anthony YUEN, MH, JP <br />\r\n</strong>Managing Director, LIS Wealth Management Ltd. 阮德添太平绅士、荣誉勋章- LIS 企业谘洵有限公司董事总经理 <br />\r\n<strong>Prof. ZHAO Zhongxiu PhD</strong> <br />\r\nDean, School of International Trade and Economics University of International Business&nbsp;&nbsp;&nbsp;&nbsp; and Economics, China 赵忠秀教授、博士 - 对外经济贸易大学国际经济贸易学院院长\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>八、晋升IFA有二种途径</strong><br />\r\n•&nbsp;途径一：6门考试，适合具有中国注册会计师或中国会计师资格人士，费用19800元人 <br />\r\n•&nbsp;途径二：2门考试，适合具有注册财务策划师资格人士，费用8900元/人 <br />\r\n<strong>咨询电话</strong>：010-64492161、64494179&nbsp;\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<strong>九、证书样本</strong><br />\r\n<strong>英国注册会计师AFA执业资格证书</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<img border=\"0\" src=\"http://uibe.iarfp.org.cn/chinese/DataBase/UploadFile/20100826171347679.jpg\" style=\"border-style:none;\" /> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;line-height:21px;color:#013E41;font-family:宋体, Arial, Helvetica, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>','','','',6,0,'2014-10-31 16:58:11',0,'本站','管理员','2014-10-31',''),(11,'理念及使命','','','','',37,0,'2014-11-03 14:43:00',0,'本站','管理员','2014-11-03',''),(12,'理念及使命','<span style=\"white-space:nowrap;color:#3E82C3;\"><strong>宗旨</strong></span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">作为全球首屈一指的财务策划专业团体，提升行业的专业水平以及为公众提供财务策划的最高标准。</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;color:#3E82C3;\"><strong>使命</strong></span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">使命一: </span><br />\r\n<span style=\"white-space:nowrap;\">在制订财务策划行业专业方面扮演最高标准的角色，并致力推广及执行有关标准；</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">使命二:</span><br />\r\n<span style=\"white-space:nowrap;\">促进IARFP及国际认可的资格认证，成为财务策划师的最高专业水平的标志；</span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;\">使命三:</span><br />\r\n<span style=\"white-space:nowrap;\">推动各业界的领袖维持财务策划行业的最高标准，IARFP会员代表世界顶尖的财务策划水平；</span><br />\r\n<span style=\"white-space:nowrap;\"> </span><br />\r\n<span style=\"white-space:nowrap;\">使命四: </span><br />\r\n<span style=\"white-space:nowrap;\">提升公众对财务策划重要性的认识，并鼓励及提供座谈会发展财务策划师的专业知识，保持先进性。</span><br />\r\n<span style=\"white-space:nowrap;\"> </span><br />\r\n<span style=\"white-space:nowrap;\"> <span style=\"color:#3e82c3;\"></span></span><br />','','','',39,0,'2014-11-03 15:43:52',0,'本站','管理员','2014-11-03',''),(13,'背景','<p>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:Arial;\">R.F.P.(Registered Financial Planner) is a professional financial planning designation and was first used in commerce in 1984 and it has been the hallmark of the professional financial planner in the United Kingdom since then.<br />\r\n</span>注册财务策划师RFP专业名衔始用于1984年，从那时起，在英国一直被尊誉为专业财务策划与水平的标志。目前除英国外，RFP 会员遍布80多个国家和地区，人数超过68000人。<br />\r\n<br />\r\n目的是为财务策划从业员制定和保持最高专业标准，实行自我监管，以确保客户获得妥善的服务，保障利益。</span>\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"></span>&nbsp;\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"></span>&nbsp;\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;color:#0968f7;\"><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\">IARFP资格认证发展里程碑</span><br />\r\n</span><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\">国际财务策划师公会&nbsp;<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:Arial;\">IARFP(INTERNATIONAL ASSOCIATION OF REGISTERED FINANCIAL PLANNERS)</span>是全球范围颁授注册财务策划师资格名衔的专业机构，也是世界上唯一使用<img border=\"0\" src=\"http://www.rfp.org.cn/upImgFile/2012321103750376.gif\" width=\"69\" height=\"18\" style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;width:63px;height:18px;\" />，最早制定并颁布IARFP职业标准的团体之一。本会的主要宗旨是为社会提供专业认可的财务策划人才，并为这些专业的财务策划师授予注册会员资格。</span>\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"></span>&nbsp;\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"></span>&nbsp;\r\n	</div>\r\n	<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n		<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;color:#0968f7;\"><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:黑体;\">RFP<sup>IA</sup></span>的内涵&nbsp;</span></span><br />\r\n<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"><span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:Arial;\">RFP,Registered Financial Planners,</span>具有IA标志的注册财务策划师，是唯一符合IARFP国际执业资格标准的权威认证标志。获得RFP资格人士是真正达到RFP国际会员标准（非地区标准），是“财务专家”的专业象征。</span>\r\n	</div>\r\n</p>\r\n<div align=\"left\" style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"vertical-align:baseline;font-weight:inherit;font-style:inherit;outline:0px;padding:0px;margin:0px;border:0px;font-family:宋体;\"></span> \r\n</div>','','','',40,0,'2014-11-03 15:45:26',0,'本站','管理员','2014-11-03',''),(14,'荣誉顾问','<strong style=\"vertical-align:baseline;outline:0px;padding:0px;margin:0px;border:0px;color:#2F2F2F;font-family:Arial, Verdana, 宋体;line-height:20px;white-space:normal;background-co